    private void btnMostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMostrarActionPerformed
        Livros l = this.MontarLivros();
        JOptionPane.showMessageDialog(null, l.getTitulo()+" "+l.getAutor()+" "+
                l.getISBN()+" "+l.getEditora()+" "+l.getAnoPubli()+" "+l.getGenero()+
                " "+l.getNumPag()+" "+l.getIdioma()+" "+l.getLocBiblioteca());
    }//GEN-LAST:event_btnMostrarActionPerformed

    private void btnAtualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAtualizarActionPerformed
       Livros l = this.MontarLivros();
        if (l != null && !jTextFCod.getText().trim().isEmpty()) {
            l.setCod(Integer.parseInt(jTextFCod.getText()));
            lcntl.update(l);
        }else{
            JOptionPane.showMessageDialog(null, "Preencha todos os campos +"
                    + "corretamente.", "Erro", JOptionPane.ERROR_MESSAGE);
}
    }//GEN-LAST:event_btnAtualizarActionPerformed

    private void btnGravarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGravarActionPerformed
         Livros l = this.MontarLivros();
        if (l != null) {
        lcntl.insert(l);
        } else {
            JOptionPane.showMessageDialog(null, "Preencha todos os campos +"
                    + " corretamente.", "Erro", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnGravarActionPerformed

    private void btnExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExcluirActionPerformed
         if(!jTextFCod.getText().trim().isEmpty()){
         int cod = Integer.parseInt(jTextFCod.getText());
         lcntl.delete(cod);
        }else{
            JOptionPane.showMessageDialog(null,"Informe novamente...");
        } 
    }//GEN-LAST:event_btnExcluirActionPerformed

    private void jMenuDadosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuDadosActionPerformed
        LivrosControle lc = new LivrosControle();
         lc.selectALL();
    }//GEN-LAST:event_jMenuDadosActionPerformed

    private void btnFecharActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnFecharActionPerformed
         this.dispose();
    }//GEN-LAST:event_btnFecharActionPerformed

