    public Livros MontarLivros(){
        
        Livros l = new Livros();
        l.setTitulo(jTextFTitulo.getText());
        l.setAutor(jTextFAutor.getText());
        l.setISBN(jTextFISBN.getText());
        l.setEditora(jTextFEditora.getText());
        l.setAnoPubli(jTextFAnoPublicacao.getText());
        l.setGenero(jTextFGenero.getText());
        l.setNumPag(jTextFNumPaginas.getText());
        l.setIdioma(jTextFIdioma.getText());
        l.setLocBiblioteca(jTextFLocBiblioteca.getText());
        return l;
    }
    
